
CREATE TABLE
    Refunds (
        ord_id INTEGER NOT NULL,
        prod_name VARCHAR(200) NOT NULL,
        reason TEXT,
        dec_time TIMESTAMPTZ,
        reg_time TIMESTAMPTZ,
        ref_status VARCHAR(22) CHECK (
            ref_status IN (
                'Awaiting refund review',
                'Approved refund',
                'Rejected refund'
            )
        ),
        PRIMARY KEY (ord_id, prod_name)
    );
